new Swiper(".mySwiper-main",{direction:"vertical",slidesPerView:1,spaceBetween:30,mousewheel:!0,scrollbar:{el:".swiper-scrollbar",hide:!1,dragPosition:1},loop:!0,navigation:{nextEl:".swiper-button-next"}});new Swiper(".mySwiper-news",{slidesPerView:4,spaceBetween:30,loop:!0,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},breakpoints:{2560:{slidesPerView:5},1440:{slidesPerView:4},1024:{slidesPerView:3},768:{slidesPerView:3},425:{slidesPerView:1},375:{slidesPerView:1},320:{slidesPerView:1}}});new Swiper(".mySwiper-sales",{slidesPerView:1,scrollbar:{el:".swiper-scrollbar-sales",hide:!1}});const e=document.querySelector(".burger"),i=document.querySelector("body"),s=document.querySelector(".menu"),r=document.querySelector(".close-menu");e.addEventListener("click",()=>{e.classList.toggle("active"),i.classList.toggle("locked"),s.classList.toggle("active")});r.addEventListener("click",()=>{e.classList.remove("active"),s.classList.remove("active")});
